import useWarning from '../use-warning';
import { __DEV__ } from '../utils/assertion';
import { deepMergeObject } from '../utils/object';
import darkTheme from './dark';
import lightTheme from './default';
export const mergeTheme = (current, custom) => {
  if (!custom) return current;

  if (typeof custom === 'function') {
    const merged = custom(current);

    if ((!merged || typeof merged !== 'object') && __DEV__) {
      useWarning('The theme function must return object value.');
    }

    return merged;
  }

  return deepMergeObject(current, custom);
};
export const getThemeByType = type => {
  const themes = {
    light: lightTheme,
    dark: darkTheme
  };
  return themes[type];
};
export const switchTheme = mergedTheme => {
  return { ...getThemeByType(mergedTheme.type),
    ...mergedTheme
  };
};
