import { getNormalColor, invertHex, hexFromString } from './../utils/color';
export const getColors = (color, textColor, palette) => {
  let bgColor = getNormalColor(color, palette, palette.background);
  bgColor = bgColor.includes('linear-gradient') ? hexFromString(bgColor, palette.text, true) : bgColor;
  const labelColor = textColor && textColor !== 'default' ? getNormalColor(textColor, palette) : invertHex(bgColor);
  return {
    color: labelColor,
    bgColor
  };
};
