export const getSizes = size => {
  const sizes = {
    mini: {
      width: '32px',
      height: '18px'
    },
    small: {
      width: '40px',
      height: '20px'
    },
    medium: {
      width: '45px',
      height: '24px'
    },
    large: {
      width: '52px',
      height: '28px'
    },
    xlarge: {
      width: '62px',
      height: '32px'
    }
  };
  return sizes[size];
};
